if nargin < 1
  csize = size(get(gcf,'Colormap'),1);
end

nodes = [ 1.562500e-02	0.000000e+00;...
          5.000000e-01	0.000000e+00;...
          1.000000e+00	1.000000e+00];
nodes(:,1) = round(nodes(:,1)*csize);
nodes(1,1) = 1;
for i = 1:size(nodes,1)-1
  W = lspace(nodes(i,2),nodes(i+1,2),nodes(i+1,1)-nodes(i, 1)+1);
  map(1,nodes(i,1):nodes(i+1,1)-1) = W(1:nodes(i+1,1)- nodes(i,1));
end
map(1,nodes(i+1,1)) = nodes(i+1,2);

nodes = [ 1.562500e-02	5.028736e-01;...
          5.000000e-01	0.000000e+00;...
          1.000000e+00	5.028736e-01];
nodes(:,1) = round(nodes(:,1)*csize);
nodes(1,1) = 1;
for i = 1:size(nodes,1)-1
  W = lspace(nodes(i,2),nodes(i+1,2),nodes(i+1,1)-nodes(i, 1)+1);
  map(2,nodes(i,1):nodes(i+1,1)-1) = W(1:nodes(i+1,1)- nodes(i,1));
end
map(2,nodes(i+1,1)) = nodes(i+1,2);

nodes = [ 1.562500e-02	1.000000e+00;...
          5.000000e-01	2.873563e-03;...
          1.000000e+00	0.000000e+00];
nodes(:,1) = round(nodes(:,1)*csize);
nodes(1,1) = 1;
for i = 1:size(nodes,1)-1
  W = lspace(nodes(i,2),nodes(i+1,2),nodes(i+1,1)-nodes(i, 1)+1);
  map(3,nodes(i,1):nodes(i+1,1)-1) = W(1:nodes(i+1,1)- nodes(i,1));
end
map(3,nodes(i+1,1)) = nodes(i+1,2);

nodes = [ 1.562500e-02	1.000000e+00;...
          1.000000e+00	1.000000e+00];
nodes(:,1) = round(nodes(:,1)*csize);
nodes(1,1) = 1;
for i = 1:size(nodes,1)-1
  W = lspace(nodes(i,2),nodes(i+1,2),nodes(i+1,1)-nodes(i, 1)+1);
  map(4,nodes(i,1):nodes(i+1,1)-1) = W(1:nodes(i+1,1)- nodes(i,1));
end
map(4,nodes(i+1,1)) = nodes(i+1,2);

map = (map(1:3,:) .* repmat(map(4,:),3,1))';

